import React, { useEffect, useState } from 'react'
import data from "../resources/data.json"
import img1 from "./logo192.png"
import { useNavigate,useParams } from 'react-router-dom'
import axios from 'axios';

export default function ProfileDetail() {
    const {id}=useParams();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        profileImage: null,
        name: '',
        phone: '',
        bio: '',
        location: '',
        profession: '',
        skills: '',
      });

    useEffect(()=>{
        axios.get("http://localhost:3030/candidate/"+id)
        .then(res =>setFormData(res.dataset))
        .catch(err=> console.log(err));
    },[])

    return (
        <ul class="container" style={{justifyContent:'center', alignItems:'center',
            display: 'flex', flexWrap: "wrap", backgroundImage: "URL('https://t4.ftcdn.net/jpg/04/61/47/03/360_F_461470323_6TMQSkCCs9XQoTtyer8VCsFypxwRiDGU.jpg')",
            backgroundSize: 'cover'}}>
                <img src={img1} class="card-img-top" alt="" style={{height:'80%%',width:'100%'}}/>
                <div class="card-body">
                    <h5 class="card-title"> {formData.name} </h5>
                    <p class="card-text">{formData.bio}</p>
                    
                </div>
        </ul>
    )
}
